mapper.select:
SELECT id, owner, education, graduate_time `graduateTime`, file_name `fileName`, file_type `fileType`, file_path `filePath`, dest_name `destName`, uploader_id `uploaderId`, upload_time `uploadTime` FROM resume
AND graduate_time >= #{options.from}
AND graduate_time <= #{options.to}
AND education=#{options.keyword} OR aply_project LIKE concat('%',concat(#{options.keyword},'%')) OR owner LIKE concat('%',concat(#{options.keyword},'%'))
ORDER BY upload_time DESC limit #{start},#{size};

SELECT
COUNT(id)
FROM resume


        AND graduate_time >= #{options.from}


        AND graduate_time <= #{options.to}



        AND education=#{options.keyword}
        OR aply_project LIKE concat('%',concat(#{options.keyword},'%'))
        OR owner LIKE concat('%',concat(#{options.keyword},'%'))

mapper.insert:
SELECT replace(uuid(), '-', '') FROM dual;
INSERT INTO resume( id,owner,education,major,graduate_time,dpt_id,file_name,file_type,file_path,dest_name,uploader_id,upload_time ) VALUES
(#{id, jdbcType=VARCHAR}, #{owner, jdbcType=VARCHAR}, #{education, jdbcType=VARCHAR}, #{major, jdbcType=VARCHAR}, #{graduateTime, jdbcType=DATE}, #{dptId}, #{fileName, jdbcType=VARCHAR}, #{fileType, jdbcType=VARCHAR}, #{filePath, jdbcType=VARCHAR}, #{destName, jdbcType=VARCHAR}, #{uploaderId, jdbcType=VARCHAR}, now())

mapper.update:
UPDATE resume SET owner = #{owner, jdbcType=VARCHAR}, graduate_time = #{graduateTime, jdbcType=DATE}, education = #{education, jdbcType=VARCHAR}, major = #{major, jdbcType=VARCHAR}, dpt_id = #{dptId}
,file_name = #{fileName, jdbcType=VARCHAR}, file_path = #{filePath, jdbcType=VARCHAR}, file_type = #{fileType, jdbcType=VARCHAR}, dest_name = #{destName, jdbcType=VARCHAR}, upload_time = now(), uploader_id = #{uploadId, jdbcType=VARCHAR}
WHERE id = #{resumeId, jdbcType=VARCHAR}

mapper.select:
select r.id, r.owner, r.major, r.education, r.file_name `fileName`, r.file_type `fileType`, r.file_path `filePath`, r.dest_name `destName`, r.uploader_id `uploaderId`, r.upload_time `uploadTime`, r.graduate_time `graduateTime`, d.dpt_name `dptName`, p.pro_name `proName`, d2.dpt_name `ctrName` from resume r LEFT join dpt_prj_res_mid m on r.id=m.res_id left join dpt_prj_mid dp on m.dpt_prj_id=dp.id left join department d on dp.dpt_id=d.id left join project p on dp.prj_id=p.id LEFT JOIN department d2 on r.dpt_id=d2.id
AND r.graduate_time >= #{options.from}
AND r.graduate_time <= #{options.to}
AND r.owner LIKE concat('%', concat(#{options.keyword},'%')) OR r.education LIKE concat('%', concat(#{options.keyword},'%')) OR r.major LIKE concat('%', concat(#{options.keyword},'%')) OR d.dpt_name LIKE concat('%', concat(#{options.keyword},'%')) OR d2.dpt_name LIKE concat('%', concat(#{options.keyword},'%')) OR p.pro_name LIKE concat('%', concat(#{options.keyword},'%'))
ORDER BY r.upload_time DESC,r.owner limit #{start},#{size};

select count(1) from resume r LEFT join dpt_prj_res_mid m on r.id=m.res_id left join dpt_prj_mid dp on m.dpt_prj_id=dp.id left join department d on dp.dpt_id=d.id left join project p on dp.prj_id=p.id LEFT JOIN department d2 on r.dpt_id=d2.id
AND r.graduate_time >= #{options.from}
AND r.graduate_time <= #{options.to}
AND r.owner LIKE concat('%', concat(#{options.keyword},'%')) OR r.education LIKE concat('%', concat(#{options.keyword},'%')) OR r.major LIKE concat('%', concat(#{options.keyword},'%')) OR d.dpt_name LIKE concat('%', concat(#{options.keyword},'%')) OR d2.dpt_name LIKE concat('%', concat(#{options.keyword},'%')) OR p.pro_name LIKE concat('%', concat(#{options.keyword},'%'))
